        {
            PlayerPrefs.SetString("Stats", Stats);
            PlayerPrefs.SetString("Vehicle_GIzwn", Vehicle_GIzwn);
            PlayerPrefs.SetString("VehiclesList", VehicleList);
            PlayerPrefs.SetInt("Opened", 1);
            PlayerPrefs.SetInt("Tutorial", 1);
            PlayerPrefs.SetString("GeneratedName", "QuickHawk248");
            PlayerPrefs.SetString("SH", "df1f2f018b2db8778e06561bfbfc850c");
            PlayerPrefs.SetInt("UseFBName", 0);
            PlayerPrefs.Save();
            DataStore.SetBool("StuntParkUnlocked", true);   
            sceneToLoad = "StuntPark";
        }
    }
    private void Start()
    {
        LoadTutorial();
        StartCoroutine(LoadingCoroutine());
    }

    private IEnumerator LoadingCoroutine()
    {
        float elapsedTime = 0f;
        float incrementTime = loadingTime / 10f;
        float currentFillAmount = 0f;

        if(sceneToLoad == "") yield break;

		AsyncOperation asyncOperation = SceneManager.LoadSceneAsync(sceneToLoad);
        asyncOperation.allowSceneActivation = false;


        while (elapsedTime < loadingTime)
        {
            elapsedTime += incrementTime;
            currentFillAmount += Random.Range(0.1f, 0.2f);

            if (currentFillAmount > 1f)
            {
                currentFillAmount = 1f;
            }
			
			if(elapsedTime > loadingTime * 0.9f)
				asyncOperation.allowSceneActivation = true;

            loadingBar.fillAmount = currentFillAmount;

            yield return new WaitForSeconds(incrementTime);
        }

        loadingBar.fillAmount = 1f;

    }

}
